'use strict';

module.exports = function getRoutes(routesConfigPaths) {
    return (url) => {
        let routePaths = require(routesConfigPaths);
        if (routePaths && routePaths.routes) {
            for (let index = 0; index < routePaths.routes.length; index++) {
                let path = routePaths.routes[index].path;
                let service = routePaths.routes[index].service;
                if (path &&service&& path === url) {
                    return service;
                }
            }
        }
    };
};
