'use strict';
const Transform = require('./transform');

module.exports = function parseTemplate (handledTags, insertBeforePipeTags) {
    return (baseTemplate, childTemplate,request,getServiceUrl) => new Promise((resolve) => {
        const transform = new Transform(handledTags, insertBeforePipeTags);
        const serializedList = transform.applyTransforms(baseTemplate, childTemplate,request,getServiceUrl);
        resolve(serializedList);
    });
};
